import { Link } from 'react-router-dom'
import logo from '../images/logo.svg'
import styled from 'styled-components';
import Button from './Button';
import React from 'react';
import { Dropdown } from 'react-bootstrap';
import { useState } from 'react';
import testProfileImg from '../images/testProfileImg.jpeg'

export default function NavBar() {
  var isUserAuthenticated = localStorage.getItem("authToken") !== null;

  const [showMobileNav, setShowMobileNav] = useState(false);

  const userLogout = () => {
    localStorage.clear();
    isUserAuthenticated = false;
  }

  const searchIcon = {
    left: '20px', pointerEvents: 'none', color: 'var(--base-50)', top: '50%', transform: 'translateY(-50%)', position: 'absolute'
  }

  const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
    <a
      href=""
      ref={ref}
      onClick={(e) => {
        e.preventDefault();
        onClick(e);
      }}
    >
      <RoundIconButton>
        <img style={{height: '40px', borderRadius: 'var(--br-round)'}} src={testProfileImg} alt="" />
      </RoundIconButton>
    </a>
  ));

  const DropdownMenu = styled.div`
    border-radius: var(--br-md);
    padding: 4px;
    width: 240px;
    border: none;
    transform: translate(-180px, 70px)!important;
    box-shadow: var(--shadow-md);

    @media (max-width: 800px) {
      margin: 0px;
      min-width: calc(100vw - 48px);
      box-shadow: var(--shadow-md);
      position: absolute;
      transform: translate(-40vw, 70px)!important;
      right: 0px;
      z-index: 9999;
    }
  `;

  const MenuItem = styled.li`
    border-radius: var(--br-md);
    padding: 12px 8px;
    font-weight: bold;
    color: var(--base);
    background-color: var(--base-10);
    transition: 200ms ease-out;
    cursor: pointer;

    &:hover {
      background-color: var(--accent-pink-a10);
      color: var(--accent-pink);
    }
  `;

  const CustomMenu = React.forwardRef(
    ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {

      const iconStyle = {
        transform: 'translateY(2px)',
        marginRight: '12px'
      }

      return (
        <DropdownMenu
          ref={ref}
          style={style}
          className={className}
          aria-labelledby={labeledBy} 
        >
          <ul className="list-unstyled m-0" style={{display: 'grid', gap: '4px'}}>
            <div style={{paddingBottom: '4px', borderBottom: '1px solid var(--base-20)'}}>
              <Link to="/profile">
                <MenuItem>
                  <h4>Warren Buffett</h4>
                  <span style={{color: 'var(--base-50)', fontWeight: '400', fontSize: 'var(--fs-b3)'}}>@warrenbuffett</span>
                </MenuItem>
              </Link>
            </div>
            <MenuItem>
              <i className='bx bx-sm bx-fw bx-wallet-alt' style={iconStyle}></i>
              My Wallet
            </MenuItem>
            <MenuItem>
              <i className='bx bx-sm bx-fw bx-heart' style={iconStyle}></i>
              My Likes
            </MenuItem>
            <MenuItem>
              <i className='bx bx-sm bx-fw bx-cog' style={iconStyle}></i>
              Settings
            </MenuItem>

            <div style={{paddingTop: '4px', borderTop: '1px solid var(--base-20)'}}>
              <MenuItem>
                <i className='bx bx-sm bx-fw bx-support' style={iconStyle}></i>
                Support
              </MenuItem>
            </div>
            <MenuItem onClick={userLogout}>
              <i className='bx bx-sm bx-fw bx-log-out' style={iconStyle}></i>
              Sign out
            </MenuItem>
          </ul>
