import React, { useState } from 'react';
import styles from './button.module.css';

const Button = ({ children, onClick, btnColor = 'teal', labelColor, disabled, type, style, ...props }) => {
	const [
		hover,
		setHover
	] = useState(false);
	const toggleHover = () => {
		setHover(!hover);
	};
	const commonStyles = {
		backgroundColor : btnColor,
		color           : labelColor || 'white'
	};
	const outlineStyles = {
		border          : `1px solid ${btnColor}`,
		color           : btnColor,
		backgroundColor : 'white'
	};
	const outlineHoverStyle = {
		color           : labelColor || 'white',
		backgroundColor : btnColor
	};

	const roundedStyle = {
		backgroundColor : btnColor,
		color           : labelColor || 'white',
		borderRadius    : '25px'
	};
	const disabledStyle = {
		cursor          : 'default',
		backgroundColor : btnColor,
		color           : labelColor || 'white',
		opacity         : 0.4
	};
	const blockStyles = {
		width  : '95%',
		margin : '0 auto'
	};
	let btnStyle;
	switch (type) {
		case 'rounded':
			btnStyle = roundedStyle;
			break;
		case 'block':
			btnStyle = blockStyles;
			break;
		case 'outline':
			if (hover) {
				btnStyle = outlineHoverStyle;
			}
			else {
				btnStyle = outlineStyles;
			}
			break;
		default:
			btnStyle = {
				backgroundColor : btnColor,
				color           : labelColor || 'white'
			};
			break;
	}
	return (
		<ShillyButton
			style={
					disabled ? { ...commonStyles, ...btnStyle, ...disabledStyle, ...style } :
					{ ...commonStyles, ...btnStyle, ...style }
			}o
			onMouseEnter={toggleHover}
			onMouseLeave={toggleHover}
			{...props}
			type="button"
			onClick={

					!disabled ? onClick :
					() => {}
			}
			className={styles.btn}
		>
			{children || 'button'}
		</ShillyButton>
	);
};

const ShillyButton = styled.div`
  font-weight: bold;
  transition: 200ms ease-out;
  border-radius: var(--br-lg);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  cursor: pointer;
  padding: 16px 24px;
`;

export default Button;
