import { Row, Col } from "react-bootstrap";
import { useState, useEffect } from "react";
import styled from "styled-components";
import logo from "../../images/logo.svg";
import Button from "../../components/Button";
import GoogleIcon from "../../images/GoogleIcon.svg";
import axios from "axios";

export default function Signup({ history }) {
  const [page, setPage] = useState(0);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const [role, setRole] = useState(undefined);

  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [location, setLocation] = useState("");


  useEffect(() => {
    if (localStorage.getItem("authToken")) {
      history.push("/");
    }
  }, [history]);

  const registerHandler = async (e) => {
    const config = {
      header: { "Content-Type": "application/json" },
    };

    try {
      const { data } = await axios.post(
        `${process.env.REACT_APP_SERVER_URI}/api/auth/signup`,
        { username, email, password },
        config
      );

      localStorage.setItem("authToken", data.token);
      history.push();
    } catch (error) {
      setError(error.response.data.error);
      setTimeout(() => {
        setError("");
      }, 5000);
    }
  };


  const signupForm = (
    <form>
      <div style={{ display: "grid", gap: "16px" }}>
        <Row className="align-items-center">
          <Col md={5} className="mb-4 mb-md-0">
            <h3>Username</h3>
          </Col>
          <Col md={7}>
            <input
              type="text"
              tabIndex={1}
              required
              id="name"
              name="username"
              placeholder="Enter username"
              onChange={(e) => setUsername(e.target.value)}
              value={username}
            />
          </Col>
        </Row>

        <Row className="align-items-center">
          <Col md={5} className="mb-4 mb-md-0">
            <h3>Email address</h3>
          </Col>
          <Col md={7}>
            <input
              type="email"
              tabIndex={2}
              required
              id="email"
              name="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Col>
        </Row>

        <Row className="align-items-center">
          <Col md={5} className="mb-4 mb-md-0">
            <h3>Password</h3>
          </Col>
          <Col md={7}>
            <input
              type="password"
              tabIndex={3}
              required
              id="password"
              name="password"
              placeholder="Enter password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Col>
        </Row>

        <div className="d-flex w-100 justify-content-end">
          <h4 style={{ color: "var(--accent-pink)" }}> Forgot password? </h4>
        </div>

        {error && <span style={{fontWeight: 'bold', color: 'var(--accent-error)' }}>{error}</span>}
      </div>

      <Button onClick={() => setPage(1)} tabIndex={4} variant="primary" style={{ width: "100%", marginTop: "48px" }} >
        Sign up
      </Button>
      <div style={{ color: "var(--base-50)", textAlign: "center", margin: "16px 0", }}>
        Or
      </div>
      <Button variant="tertiary" style={{ width: "100%" }}>
        <img src={GoogleIcon} style={{ height: "20px", marginRight: "16px" }}/>
        Sign up with Google
      </Button>
    </form>
  );

  const pageOne = (
    <CardPageWrap>
      <Header>Welcome to Shilly!</Header>
      <CardWrap>
        <Card>
          {signupForm}

          <div className="mt-5">
            Already have an account?
            <a href="/login" style={{ color: "var(--accent-pink)" }}> {" "} Log in </a>
          </div>
        </Card>
      </CardWrap>
    </CardPageWrap>
  );

  function RoleOption(roleType, title, subtitle, emoji) {
    // for role, 0 for shiller, 1 for token
    return <RoleOptionCard onClick={() => setRole(roleType)} style={{ transition: 'var(--transition)', opacity: roleType != role && role != undefined ? '0.5' : '1', boxShadow: roleType == role ? '0 0 0 2px var(--accent-pink)' : '0 0 0 1px var(--base-30)' }}>
      <div style={{marginRight: '32px', fontSize: '32px'}}>{ emoji }</div>
      <div>
        <h3>{title}</h3>
        <p style={{color: 'var(--base-70)', margin: '0', marginTop: '8px'}}>{subtitle}</p>
      </div>
    </RoleOptionCard>
  }

  const RoleOptionCard = styled.div`
    cursor: pointer; 
    border-radius: var(--br-md); 
    padding: 24px 24px 24px 32px; 
    display: flex; 
    align-items: center; 
    box-shadow: 0 0 0 1px var(--base-30);
    transition: var(--transition);

    &:hover {
      box-shadow: 0 0 0 2px var(--accent-pink)!important;
    }
  `;

  const roleSelectionPage = (
    <CardPageWrap>
      <Header>What brings you to Shilly?</Header>
      <p style={{ textAlign: 'center', marginTop: '16px', color: 'var(--base-50)' }}>Select the role that best describes you.</p>
      <CardWrap>
        <Card>
          <div style={{display: 'grid', gap: '24px', marginBottom: '64px'}}>
            {RoleOption(0, "I'm a Shiller", "You’re a crypto influencer. Find, connect and collaborate with up-and-coming tokens.", '🥳')}
            {RoleOption(1, "I'm a Token creator", "You’re looking for Shillers to promote your exciting new coin.", '🚀')}
          </div>
          <div style={{display: 'flex', width: '100%', justifyContent: 'center'}}>
            <Button onClick={() => setPage(2)} variant="primary" disabled={role == undefined} style={{padding: '16px 40px'}}>
              Next
              <box-icon name='chevron-right' size="sm" color="var(--base-10)"></box-icon>
            </Button>
          </div>
        </Card>
      </CardWrap>
    </CardPageWrap>
  );

  const detailsPage = (
    <CardPageWrap>
      <Header>Almost there!</Header>
      <p style={{ textAlign: 'center', marginTop: '16px', color: 'var(--base-50)' }}>Let’s create your profile. You can do this later on too.</p>
      <CardWrap>
        <Card>
          <form onSubmit={registerHandler}>
            <div style={{ display: "grid", gap: "48px", marginBottom: '64px' }}>
              <Row className="align-items-center">
                <Col md={5} className="mb-4 mb-md-0">
                  <h3>{role == 0 ? "What's your name?" : "What's your token called?"}</h3>
                </Col>
                <Col md={7}>
                  <input
                    type="text"
                    tabIndex={1}
                    required
                    name="name"
                    placeholder={role == 0 ? 'Your name here' : 'Your token name here'}
                    onChange={(e) => setName(e.target.value)}
                    value={name}
                  />
                </Col>
              </Row>

              <Row className="align-items-center">
                <Col md={5} className="mb-4 mb-md-0">
                  <h3>{role == 0 ? 'Choose a username' : "Your token's symbol" }</h3>
                </Col>
                <Col md={7}>
                  <input
                    type="username"
                    tabIndex={2}
                    required
                    id="username"
                    name="username"
                    placeholder={role == 0 ? 'Your username here' : 'DOGE'}
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                  />
                </Col>
              </Row>

              <Row className="align-items-center">
                <Col md={5} className="mb-4 mb-md-0">
                  <h3>Where are you located?</h3>
                </Col>
                <Col md={7}>
                  <input
                    type="text"
                    tabIndex={3}
                    id="location"
                    name="location"
                    placeholder="Stockholm, Sweden"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                  />
                </Col>
              </Row>

              {error && <span>{error}</span>}
            </div>

            <div style={{ display: 'flex', width: '100%', justifyContent: 'center' }}>
              <Button type="submit" tabIndex={4} variant="primary" style={{ padding: '16px 40px', marginTop: "48px" }}>
                Finish
                <box-icon name='chevron-right' size="sm" color="var(--base-10)"></box-icon>
              </Button>
            </div>
          </form>
        </Card>
      </CardWrap>
    </CardPageWrap>
  );

  return (
    <div
      style={{
        backgroundColor: "var(--base-20)",
        height: '100%',
        width: "100vw",
        overflowX: "hidden",
      }}
    >
      <div className="d-flex w-75 m-auto" style={{ overflowX: "hidden" }}>
        <a href="/">
          <img src={logo} alt="logo" style={{ height: "41px", margin: "60px 0 40px 0", cursor: "pointer" }} />
        </a>
      </div>
      {page == 0 && pageOne }
      {page == 1 && roleSelectionPage}
      {page == 2 && detailsPage}
    </div>
  );
}

const Header = styled.h1`
  margin: 0 auto;
  max-width: 700px;
  text-align: center;
`;

const CardPageWrap = styled.div`
  width: 100%;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;
  padding: 0 24px;
  overflow-x: hidden;
`;

const CardWrap = styled.div`
  display: grid;
  margin: 0;
  gap: 24px;
  padding: 24px 0;

  @media (min-width: 640px) {
    padding: 32px 0;
  }

  @media (min-width: 832px) {
    padding: 64px 48px;
  }
`;

const Card = styled.div`
  display: block;
  padding: 24px;
  box-shadow: var(--shadow);
  background-color: var(--base-10);
  max-width: 630px;
  border-radius: var(--br-lg);

  @media (min-width: 640px) {
    padding: 32px;
  }

  @media (min-width: 832px) {
    padding: 32px 48px;
    margin: 0 auto;
    width: 100%;
  }
`;
