import React, { useState } from "react";
import Axios from "axios";
import styled from "styled-components";

const ImageUpload = () => {
  const [fileData, setFileData] = useState();
  const [images, setFile] = useState("");
  const [tempImgUrl, setTempImgUrl] = useState("");

  const handleFileChange = ({ target }) => {
    setFileData(target.files[0]);
    console.log(target.files[0])
    setTempImgUrl(URL.createObjectURL(target.files[0]));
    setFile(target.value);
  };

  const removeFile = () => {
    if (fileData) {
      console.log("hi")
      setFileData();
      setFile("");
      setTempImgUrl("");
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formdata = new FormData();

    formdata.append("image", fileData);
    console.log("submitting....", fileData);

    await Axios.post(`${process.env.REACT_APP_SERVER_URI}/api/uploadImage/image`, formdata)
      .then((res) => console.log("res RESRES", res))
      .catch((error) => console.error(error));
  };



  return (
    <form onSubmit={handleSubmit}>
      <UploadFileArea htmlFor={'upload-button'} tempImgUrl={tempImgUrl} style={{background: fileData ? `url(${tempImgUrl})` : 'white' }}>
        {!fileData && <box-icon name='camera' color='var(--base-30)'></box-icon>}
      </UploadFileArea>
      {fileData && <RemoveImgButton onClick={removeFile}>
        <i className="bx bx-trash-alt trash"></i>
      </RemoveImgButton>}
      <input
        id="upload-button"
        type="file"
        value={images}
        name="file"
        accept="image/*"
        onChange={handleFileChange}
        placeholder="upload image"
        style={{display: 'none'}}
      />

      {/* <button>submit</button> */}
    </form>
  );
};

export default ImageUpload;

const UploadFileArea = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: var(--br-round);
  border: ${props => props.tempImgUrl !== "" ? 'none' : '2px dashed var(--base-30)'};
  width: 100px;
  height: 100px;
  background-size: cover!important;
  background-position: center center!important;
  background-repeat: no-repeat;
  cursor: pointer;
  transition: var(--transition);

  &:hover {
    border: ${props => props.tempImgUrl !== "" ? 'none' : '2px dashed var(--accent-pink)'};
    // opacity: ${props => props.tempImgUrl !== "" ? '0.7': '1' };
    filter: ${props => props.tempImgUrl !== "" ? 'grayscale(50%)': '' }; 
  }
`;

const RemoveImgButton = styled.div`
  background-color: var(--accent-error);
  color: var(--base-10);
  height: 32px;
  width: 32px;
  border-radius: var(--br-round);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  left: 34px;
  bottom: 16px;
  border: 2px solid var(--base-10);
  cursor: pointer;
  transition: var(--transition);
  display: none;

  &:hover {
    background-color: var(--accent-error-lighter);
  }

  &:hover ${UploadFileArea} {
    display: block;
  }
`;
