// import { Container,Image,NavDropdown,Nav, Form} from 'react-bootstrap'
import { Link } from 'react-router-dom'
import logo from '../images/logo.svg'
import styled from 'styled-components';
import Button from './Button';
import { useState } from 'react';

export default function NavBar() {
//   return (
//     <>
//       <Navbar bg="light" expand="lg">
//   <Container>
//     <Navbar.Brand >
//       <Link to='/'><Image src={logo} alt="logo"/></Link>

//       </Navbar.Brand>
//     <Form.Group>
//     <Form.Control  placeholder="Search" />
//     </Form.Group>
//     <Navbar.Toggle aria-controls="basic-navbar-nav" />
//     <Navbar.Collapse id="basic-navbar-nav">
//       <Nav className="ms-auto">
//         <Nav.Link>
//           <Link to='/explore'>Explore
//           </Link>

//           </Nav.Link>
//         <Nav.Link>
//           <Link to='/login'>
//           Log In
//           </Link></Nav.Link>
//         <Nav.Link href="#signup">Sign Up</Nav.Link>
//         <NavDropdown title="Dropdown" id="basic-nav-dropdown">
//           <NavDropdown.Item href="/profile">Profile</NavDropdown.Item>
//           <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
//           <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
//           <NavDropdown.Divider />
//           <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
//         </NavDropdown>
//       </Nav>
//     </Navbar.Collapse>
//   </Container>
// </Navbar>
// </>
//   )

  const [showMobileNav, setShowMobileNav] = useState(true);

  const searchIcon = {
    left: '20px', pointerEvents: 'none', color: 'var(--base-50)', top: '50%', transform: 'translateY(-50%)', position: 'absolute'
  }

  const MobileNav = () => (
    <MobileMenu>
        <MobileMenuInner>
          <div style={{display: 'flex', flexDirection: 'column', alignItems: 'start'}}>
            <div style={{display: 'flex', alignItems: 'center', flexDirection: 'row', justifyContent: 'space-between', marginTop: '8px', width: '100%'}}>
              <img src={logo} alt="logo" style={{height: '41px'}} />

              <RoundIconButton onClick={() => { setShowMobileNav(false)}}>
                <box-icon name='x' size="md" color="var(--base-50)"></box-icon>
              </RoundIconButton>
            </div>

            <MobileNavLink href="/explore" style={{marginTop: '40px'}}>Home</MobileNavLink>
            <MobileNavLink href="/explore">Explore</MobileNavLink>
            <MobileNavLink href="/explore">About</MobileNavLink>
            <MobileNavLink href="/explore">Contact</MobileNavLink>

            <MobileNavLink href="/login" style={{marginTop: '24px'}}>Log in</MobileNavLink>
            <MobileNavLink href="/explore">Sign up</MobileNavLink>

          </div>
        </MobileMenuInner>
      </MobileMenu>
  );

  return (
    <Navbar>
      { showMobileNav ? <MobileNav /> : null }

      <NavbarInner>
        <Link to='/' style={{marginRight: '16px'}}><img src={logo} alt="logo"/></Link>
        <NavLink href="/explore">Explore</NavLink>
        <NavLink href="/explore">About</NavLink>

        <SearchBarWrap>
          <div style={{position: 'relative', zIndex: '99'}}>
            <box-icon name='search' style={searchIcon}></box-icon>
            <Input placeholder="Search Shilly..."></Input>
          </div>
          <div style={{position: 'relative'}}></div>
        </SearchBarWrap>


        { window.innerWidth > 800 ? <Button variant='secondary' style={{height: '48px'}}>Log in</Button> : null }
        { window.innerWidth > 800 ? <Button variant='primary' style={{height: '48px', marginLeft: '8px'}}>Sign up</Button> : null }

        { window.innerWidth < 800 ? 
          <RoundIconButton onClick={() => { setShowMobileNav(true)}}>
            <box-icon name='menu' size="md" color="var(--base-50)"></box-icon>
          </RoundIconButton> : null
        } 

      </NavbarInner>
    </Navbar>
  );
}

const Navbar = styled.nav`
  width: 100%;
  max-width: 1280px;
  margin-left: auto;
  margin-right: auto;
  padding: 8px 24px;

  @media (min-width: 640px) {
    padding-top: 16px;
  }
`;

const NavbarInner = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
  align-items: center;
`;

const SearchBarWrap = styled.div`
  flex: 1 1 0%;
  justify-content: center;
  position: relative;
  display: none;

  @media (min-width: 800px) {
    display: block;
    padding: 0 20px;
  }
`;

const Input = styled.input`
  background-color: white;
  box-shadow: var(--shadow);
  padding: 1px 20px 1px 48px;
  min-height: 50px;
  display: flex;
  align-items: center;
  appearance: none;
  border-radius: var(--br-round);
  border: 2px solid var(--base-30);
  transition: 200ms ease-out;
  width: 100%;

  &:focus {
    outline: none;
    border: 2px solid var(--base);
  }

  &::placeholder {
    color: var(--base-50);
  }
`;

const NavLink = styled.a`
  display: none;

  @media (min-width: 800px) {
    display: block;
    padding: 14px 12px;
    font-size: var(--fs-b2);
  }
`;

const MobileNavLink = styled.a`
  font-size: 28px;
  padding-top: 8px;
  font-weight: bold;
  display: block;

  @media (min-width: 800px) {
    display: none;
  }
`;

const MobileMenu = styled.div`
  position: fixed;
  z-index: 9999;
  top: 0;
  left: 0;
  width: 100%;
`;

const MobileMenuInner = styled.div`
  position: absolute;
  width: 100vw;
  top: 0;
  left: 0;
  height: 100vh;
  background-color: var(--base-10);
  padding: 0 24px;
`;

const RoundIconButton = styled.div`
  padding: 10px 10px 4px;
  border-radius: var(--br-round);
  box-shadow: var(--shadow); 
  background-color: white;
  transition: 200ms ease-out;
  cursor: pointer;
  display: block;

  &:hover {
    box-shadow: var(--shadow-md);
    transform: translateY(-4px);
  }

  @media (min-width: 800px) {
    display: none;
  }
`;
