import profilepic from '../images/testProfileImg.jpeg';
import coverpic from '../images/testCoverImg.jpeg';
import styled from 'styled-components';


const Card = (props) => {

    const coverImgWrapper = { 
        position: 'absolute', 
        top: '0', 
        right: '0', 
        bottom: '0', 
        left: '0', 
        backgroundColor: 'var(--base-20)', 
        display: 'flex' 
    }

    const coverImg = {
        maxWidth: '100%',
        height: 'auto',
        objectFit: 'cover',
        minHeight: '100%',
        minWidth: '100%'
    }

    const username = { 
        marginBottom: '16px', 
        color: 'var(--base-50)' 
    }

    const symbol = {
        textTransform: 'uppercase',
        color: 'var(--base-80)',
        fontWeight: 'bold',
        fontSize: 'var(--fs-b4)',
        padding: '2px 6px',
        backgroundColor: 'var(--base-20)',
        borderRadius: 'var(--br-sm)',
    }

	return (
        <CardStructure>
            <a href="#" style={{ textDecoration: 'none' }}>
                <div style={{ position: 'relative' }}>
                    <div style={{ position: 'relative' }}>
                        <div style={{ margin: '0', width: '100%', height: '0', paddingBottom: '57%' }}></div>
                        <CoverImageWrapper>
                            <img className="coverImage" src={coverpic} alt="cover"></img>
                        </CoverImageWrapper>
                    </div>
                    <div style={{ margin: '0 24px', position: 'relative' }}>
                        <ProfileImageWrapper>
                            <ProfileImage></ProfileImage>
                        </ProfileImageWrapper>
                    </div>
                </div>

                <CardBody>
                    {props.shiller && <h3>{props.shiller.name}</h3>}
                    {props.token && <h3>{props.token.name}</h3>}

                    {props.shiller && <h4 style={username}>{props.shiller.username}</h4>}
                    {props.token && <TokenSymbol>{props.token.symbol}</TokenSymbol>}

                    <p className="fs-b3" style={{ borderBottom: '1px solid var(--base-20)', paddingBottom: '24px', margin: '16px 0' }}>Bitcoin. Ethereum Crypto YouTuber, Creator of BitBoyCrypto.com, Co-Host of New Money Gang Coach</p>
                </CardBody>
            </a>
        </CardStructure>
	)
}


const CardStructure = styled.div`
    border-radius: var(--br-md);
    background-color: var(--base-10);
    box-shadow: var(--shadow);
    display: flex;
    flex-direction: column;
    overflow: hidden;
    transition: all 300ms ease-out;
    text-decoration: none;
    margin: 16px 0;

    &:hover {
        box-shadow: var(--shadow-md);
        transform: translateY(-4px);
    }
`;

const CardBody = styled.div`
    margin: 0;
    padding: 64px 24px 48px 24px;
    color: var(--base);
    text-align: left;
`;

const ProfileImageWrapper = styled.div`
    box-sizing: border-box;
    margin: 0;
    padding: 8px;
    background-color: var(--base-10);
    width: 96px;
    height: 96px;
    position: absolute;
    left: 0;
    transform: translateY(-50%);
    display: flex;
    border-radius: var(--br-round)
`;

const ProfileImage = styled.div`
    margin: 0;
    width: 80px;
    height: 80px;
    background-color: var(--base-20);
    background-size: cover;
    background-position: center center;
    border-radius: var(--br-round);
    background-image: url(${profilepic});
`;

const TokenSymbol = styled.span`
    text-transform: uppercase;
    color: var(--base-80);
    font-weight: bold;
    font-size: var(--fs-b4);
    padding: 2px 6px;
    background-color: var(--base-20);
    border-radius: var(--br-sm);
`;

const CoverImageWrapper = styled.div`
    position: absolute; 
    top: 0; 
    right: 0; 
    bottom: 0; 
    left: 0; 
    background-color: var(--base-20); 
    display: flex ;

    + .coverImage {
        height: auto;
        object-fit: cover;
        min-height: 100%;
        min-width: 100%;
        max-width: 100%;
    }
`;


export default Card;
