import { Container,Image,NavDropdown,Nav, Form} from 'react-bootstrap'
import { Link } from 'react-router-dom'
import logo from '../images/logo.svg'
import styled from 'styled-components';
import Button from './Button';

export default function NavBar() {
//   return (
//     <>
//       <Navbar bg="light" expand="lg">
//   <Container>
//     <Navbar.Brand >
//       <Link to='/'><Image src={logo} alt="logo"/></Link>

//       </Navbar.Brand>
//     <Form.Group>
//     <Form.Control  placeholder="Search" />
//     </Form.Group>
//     <Navbar.Toggle aria-controls="basic-navbar-nav" />
//     <Navbar.Collapse id="basic-navbar-nav">
//       <Nav className="ms-auto">
//         <Nav.Link>
//           <Link to='/explore'>Explore
//           </Link>

//           </Nav.Link>
//         <Nav.Link>
//           <Link to='/login'>
//           Log In
//           </Link></Nav.Link>
//         <Nav.Link href="#signup">Sign Up</Nav.Link>
//         <NavDropdown title="Dropdown" id="basic-nav-dropdown">
//           <NavDropdown.Item href="/profile">Profile</NavDropdown.Item>
//           <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
//           <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
//           <NavDropdown.Divider />
//           <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
//         </NavDropdown>
//       </Nav>
//     </Navbar.Collapse>
//   </Container>
// </Navbar>
// </>
//   )

  const searchIcon = {
    left: '20px', pointerEvents: 'none', color: 'var(--base-50)', top: '50%', transform: 'translateY(-50%)', position: 'absolute'
  }

  return (
    <Navbar>
      <MobileMenu>
        <MobileMenuInner>
          <div style={{display: 'flex', flexDirection: 'column', alignItems: 'start'}}>
            <div style={{display: 'flex', alignItems: 'center', flexDirection: 'row', justifyContent: 'space-between', marginTop: '18px', width: '100%', paddingRight: '20px'}}>
              <img src={logo} alt="logo" style={{height: '40px'}} />

              <div style={{padding: '10px 10px 4px', borderRadius: 'var(--br-round)', boxShadow: 'var(--shadow)', backgroundColor: 'white'}}>
                <box-icon name='x' size="md"></box-icon>
              </div>
            </div>

            <NavLink href="/explore" style={{marginTop: '40px'}}>Home</NavLink>
            <NavLink href="/explore">Explore</NavLink>
            <NavLink href="/explore">About</NavLink>
            <NavLink href="/explore">Contact</NavLink>

            <NavLink href="/login" style={{marginTop: '24px'}}>Log in</NavLink>
            <NavLink href="/explore">Sign up</NavLink>


          </div>
        </MobileMenuInner>
      </MobileMenu>

      <NavbarInner>
        <Link to='/' style={{marginRight: '16px'}}><img src={logo} alt="logo"/></Link>
        <NavLink href="/explore">Explore</NavLink>
        <NavLink href="/explore">About</NavLink>

        <SearchBarWrap>
          <div style={{position: 'relative', zIndex: '99'}}>
            <box-icon name='search' style={searchIcon}></box-icon>
            <Input placeholder="Search Shilly..."></Input>
          </div>
          <div style={{position: 'relative'}}></div>
        </SearchBarWrap>


        <Button variant='secondary' size='big' style={{height: '48px'}}>Log in</Button>
        <Button variant='primary' size='big' style={{marginLeft: '8px', height: '48px'}}>Sign up</Button>

      </NavbarInner>
    </Navbar>
  );
}

const Navbar = styled.nav`
  width: 100%;
  max-width: 1280px;
  margin-left: auto;
  margin-right: auto;
  padding: 8px 24px;

  @media (min-width: 640px) {
    padding-top: 16px;
  }
`;

const NavbarInner = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-between;
  align-items: center;
`;

const SearchBarWrap = styled.div`
  flex: 1 1 0%;
  justify-content: center;
  position: relative;
  display: none;

  @media (min-width: 800px) {
    display: block;
    padding: 0 20px;
  }
`;

const Input = styled.input`
  background-color: white;
  box-shadow: var(--shadow);
  padding: 1px 20px 1px 48px;
  min-height: 50px;
  display: flex;
  align-items: center;
  appearance: none;
  border-radius: var(--br-round);
  border: 2px solid var(--base-30);
  transition: 200ms ease-out;
  width: 100%;

  &:focus {
    outline: none;
    border: 2px solid var(--base);
  }

  &::placeholder {
    color: var(--base-50);
  }
`;

const NavLink = styled.a`
  font-size: var(--fs-h2);
  padding-top: 16px;
  font-weight: bold;

  @media (min-width: 800px) {
    padding: 14px 12px;
    font-size: var(--fs-b2);
  }
`;

const MobileMenu = styled.div`
  position: fixed;
  z-index: 9999;
  top: 0;
  left: 0;
  width: 100%;
`;

const MobileMenuInner = styled.div`
  position: absolute;
  width: 100vw;
  top: 0;
  left: 0;
  height: 100vh;
  background-color: var(--base-10);
  display: block;
  padding: 0 24px;

  @media (min-width: 800px) {
    display: none;
  }
`;

const RoundIconButton = styled.div`
  padding: 10px 10px 4px;
  border-radius: var(--br-round);
  box-shadow: var(--shadow); 
  background-color: white;
  transition: 200ms ease-out;

  &:hover {
    box-shadow: var(--shadow-md);
    transform: translateY(-4px);
  }
`;
