import React, { useState } from 'react';
import styled from 'styled-components';

const Button = ({ children, onClick, variant, disabled, type, style, ...props }) => {
	const [hover, setHover] = useState(false);

	const toggleHover = () => {
		setHover(!hover);
	};


	return (
		<ShillyButton
      className="primary"
			style={
					disabled ? { ...commonStyles, ...btnStyle, ...disabledStyle, ...style } :
					{ ...commonStyles, ...btnStyle, ...style }
			}
			onMouseEnter={toggleHover}
			onMouseLeave={toggleHover}
			{...props}
			type="button"
			onClick={
					!disabled ? onClick :
					() => {}
			}
		>
			{children || 'button'}
		</ShillyButton>
	);
};

const ShillyButton = styled.button`
  font-weight: bold;
  transition: 200ms ease-out;
  border-radius: var(--br-lg);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  cursor: pointer;
  padding: 16px 24px;

  & .primary {
    background-color: var(--accent-pink);
    color: var(--base-10);
  }

  & .primary:hover {
    background-color: var(--accent-pink-darker);
  }
`;

export default Button;
