import profilepic from '../images/testProfileImg.jpeg';
import coverpic from '../images/testCoverImg.jpeg';
import styled from 'styled-components';

const CardBody = styled.div`
    border-radius: var(--br-md);
    background-color: var(--base-10);
    box-shadow: var(--shadow);
    display: flex;
    flex-direction: column;
    overflow: hidden;
    transition: all 300ms ease-out;
    text-decoration: none;
    margin: 16px 0;

    &:hover {
        box-shadow: var(--shadow-md);
        transform: translateY(-4px);
    }
`;

const Card = (props) => {
    // const card = {
    //     borderRadius: 'var(--br-md)',
    //     backgroundColor: 'var(--base-10)',
    //     boxShadow: 'var(--shadow)',
    //     display: 'flex',
    //     flexDirection: 'column',
    //     overflow: 'hidden',
    //     transition: '300ms ease-in-out',
    //     textDecoration: 'none',
    //     margin: "16px 0"
    // }

    const coverImgHolder = { 
        position: 'absolute', 
        top: '0', 
        right: '0', 
        bottom: '0', 
        left: '0', 
        backgroundColor: 'var(--base-20)', 
        display: 'flex' 
    }

    const coverImg = {
        maxWidth: '100%',
        height: 'auto',
        objectFit: 'cover',
        minHeight: '100%',
        minWidth: '100%'
    }

    const pfpHolder = {
        boxSizing: 'border-box',
        margin: '0',
        padding: '8px',
        backgroundColor: 'var(--base-10)',
        width: '96px',
        height: '96px',
        position: 'absolute',
        left: '0',
        transform: 'translateY(-50%)',
        display: 'flex',
        borderRadius: 'var(--br-round)'
    }

    const pfp = {
        margin: '0',
        minWidth: '80px',
        minHeight: '80px',
        maxWidth: '80px',
        maxHeight: '80px',
        backgroundColor: 'var(--base-20)',
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        borderRadius: 'var(--br-round)',
        backgroundImage: `url(${profilepic})`
    }

    const cardBody = {
        margin: '0',
        padding: '64px 24px 48px 24px',
        color: 'var(--base)',
        textAlign: 'left'
    }

    const username = { 
        marginBottom: '16px', 
        color: 'var(--base-50)' 
    }

    const symbol = {
        textTransform: 'uppercase',
        color: 'var(--base-80)',
        fontWeight: 'bold',
        fontSize: 'var(--fs-b4)',
        padding: '2px 6px',
        backgroundColor: 'var(--base-20)',
        borderRadius: 'var(--br-sm)',
    }



	return (
        <CardBody>
            <a href="#" style={{ textDecoration: 'none' }}>
                <div style={{ position: 'relative' }}>
                    <div style={{ position: 'relative' }}>
                        <div style={{ margin: '0', width: '100%', height: '0', paddingBottom: '57%' }}></div>
                        <div style={coverImgHolder}>
                            <img style={coverImg} src={coverpic} alt="cover"></img>
                        </div>
                    </div>
                    <div style={{ margin: '0 24px', position: 'relative' }}>
                        <div style={pfpHolder}>
                            <div style={pfp}></div>
                        </div>
                    </div>
                </div>

                <div style={cardBody}>
                    {props.shiller && <h3>{props.shiller.name}</h3>}
                    {props.token && <h3>{props.token.name}</h3>}

                    {props.shiller && <h4 style={username}>{props.shiller.username}</h4>}
                    {props.token && <span style={symbol}>{props.token.symbol}</span>}

                    <p className="fs-b3" style={{ borderBottom: '1px solid var(--base-30)', paddingBottom: '24px', margin: '16px 0' }}>Bitcoin. Ethereum Crypto YouTuber, Creator of BitBoyCrypto.com, Co-Host of New Money Gang Coach</p>
                </div>
            </a>
        </CardBody>
	)
}

export default Card;
