import { Row, Col } from "react-bootstrap";
import { useState, useEffect } from "react";
import styled from "styled-components";
import logo from "../../images/logo.svg";
import Button from "../../components/Button";
import GoogleIcon from "../../images/GoogleIcon.svg";
import axios from "axios";

export default function Signup({ history }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (localStorage.getItem("authToken")) {
      history.push("/");
    }
  }, [history]);

  const registerHandler = async (e) => {
    const config = {
      header: { "Content-Type": "application/json" },
    };

    try {
      const { data } = await axios.post(
        `${process.env.REACT_APP_SERVER_URI}/api/auth/signup`,
        { username, email, password },
        config
      );

      localStorage.setItem("authToken", data.token);
      history.push();
    } catch (error) {
      setError(error.response.data.error);
      setTimeout(() => {
        setError("");
      }, 5000);
    }
  };


  const signupForm = (
    <form onSubmit={registerHandler}>
      <div style={{ display: "grid", gap: "16px" }}>
        <Row className="align-items-center">
          <Col md={5} className="mb-4 mb-md-0">
            <h3>Username</h3>
          </Col>
          <Col md={7}>
            <input
              type="text"
              tabIndex={1}
              required
              id="name"
              name="username"
              placeholder="Enter username"
              onChange={(e) => setUsername(e.target.value)}
              value={username}
            />
          </Col>
        </Row>

        <Row className="align-items-center">
          <Col md={5} className="mb-4 mb-md-0">
            <h3>Email address</h3>
          </Col>
          <Col md={7}>
            <input
              type="email"
              tabIndex={2}
              required
              id="email"
              name="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Col>
        </Row>

        <Row className="align-items-center">
          <Col md={5} className="mb-4 mb-md-0">
            <h3>Password</h3>
          </Col>
          <Col md={7}>
            <input
              type="password"
              tabIndex={3}
              required
              id="password"
              name="password"
              placeholder="Enter password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Col>
        </Row>

        <div className="d-flex w-100 justify-content-end">
          <h4 style={{ color: "var(--accent-pink)" }}>
            Forgot password?
          </h4>
        </div>

        {error && <span>{error}</span>}
      </div>

      <Button
        type="submit"
        tabIndex={4}
        variant="primary"
        style={{ width: "100%", marginTop: "48px" }}
      >
        Sign up
      </Button>
      <div
        style={{
          color: "var(--base-50)",
          textAlign: "center",
          margin: "16px 0",
        }}
      >
        Or
      </div>
      <Button variant="tertiary" style={{ width: "100%" }}>
        <img
          src={GoogleIcon}
          style={{ height: "20px", marginRight: "16px" }}
          alt=""
        />
        Sign up with Google
      </Button>
    </form>
  );

  return (
    <div
      style={{
        backgroundColor: "var(--base-20)",
        width: "100vw",
        overflowX: "hidden",
      }}
    >
      <div className="d-flex w-75 m-auto" style={{ overflowX: "hidden" }}>
        <a href="/">
          <img
            src={logo}
            alt="logo"
            style={{
              height: "41px",
              margin: "60px 0 40px 0",
              cursor: "pointer",
            }}
          />
        </a>
      </div>
      <CardPageWrap>
        <h1
          style={{ margin: "0 auto", maxWidth: "700px", textAlign: "center" }}
        >
          Welcome to Shilly!
        </h1>
        <CardWrap>
          <Card>

            {signupForm}

            <div className="mt-5">
              Already have an account?
              <a href="/login" style={{ color: "var(--accent-pink)" }}>
                {" "}
                Log in
              </a>
            </div>
          </Card>
        </CardWrap>
      </CardPageWrap>
    </div>
  );
}

const CardPageWrap = styled.div`
  width: 100%;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;
  padding: 0 24px;
  overflow-x: hidden;
`;

const CardWrap = styled.div`
  display: grid;
  margin: 0;
  gap: 24px;
  padding: 24px 0;

  @media (min-width: 640px) {
    padding: 32px 0;
  }

  @media (min-width: 832px) {
    padding: 64px 48px;
  }
`;

const Card = styled.div`
  display: block;
  padding: 24px;
  box-shadow: var(--shadow);
  background-color: var(--base-10);
  max-width: 700px;
  border-radius: var(--br-lg);

  @media (min-width: 640px) {
    padding: 32px;
  }

  @media (min-width: 832px) {
    padding: 32px 48px;
    margin: 0 auto;
    width: 100%;
  }
`;
