import {Row, Col, Container} from 'react-bootstrap'
import Card from '../components/Card';
import background from '../images/landingPageBg.png';
import axios from 'axios';


function LandingPage() {
    const hero = {
        padding: '40px 0',
    }

    const mainTitleHero = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        marginBottom: '100px',
    }

    const heroSubtitle = {
        fontSize: 'var(--fs-b1)',
        color: 'var(--base-60)',
        marginTop: '16px'
    } 

    const header = {
        width: '100%',
        paddingBottom: '20px',
        borderBottom: '1px solid var(--base-30)',
        textAlign: 'left',
        marginBottom: '24px'
    }

    const addShiller = (e) => {
        e.preventDefault();

        const shiller = {
            id: 1,
            username: '@donaldtrump',
            name: 'Donald Trump'
        };
        console.log("shiller adding", shiller)

        axios
            .post("http://localhost:5000/shiller/add", shiller)
            .then((res) => console.log(res.data))

    }

    return ( 
        <div style={{ backgroundImage: `url(${background})` }}>
            <div style={{ maxWidth: '1280px', height: '1000px', margin: 'auto', padding: '0 20px' }}>
                <section style={hero}>
                    <div style={mainTitleHero}>
                        <h1 className="fs-display" style={{color: '#490826', maxWidth: '810px'}}>
                            Bring your token <span style={{color: 'var(--accent-pink)'}}>to the moon</span> with the perfect Shiller.
                        </h1>
                        <p style={heroSubtitle}>Shilly is a platform to create, explore and connect within the crypto space.</p>
                    </div>
                </section>
                <section style={{ display: 'flex', alignItems: 'center', justifyContent: 'left', flexDirection: 'column' }}>
                    <div style={header}>
                        <h2>Trending Campaigns</h2>
                    </div>


                    <Row>
                        <Col><Card /></Col>
                        <Col><Card /></Col>
                        <Col><Card /></Col>
                        <Col><Card /></Col>
                    </Row>

                    <button onClick={addShiller}>Add</button>
                </section>
            </div>
        </div>
    );
}

export default LandingPage;
