  }


  &.tertiary {
    color: var(--base);
    background-color: transparent;
    border: 2px solid var(--base-30);
  }
  &.tertiary:hover {
    border: 2px solid var(--base-40);
  }


  &.destructive {
    color: var(--base-10);
    background-color: var(--accent-error);
  }
  &.destructive:hover {
    background-color: var(--accent-error-darker);
  }
`;

export default Button;
