import React, { useState } from 'react';
import styled from 'styled-components';

const Button = ({ children, onClick, variant, disabled, size, ...props }) => {

	return (
		// <ShillyButton
    //   className="primary"
		// 	{...props}
		// 	onClick={ !disabled ? onClick : () => {} }
		// >
		// 	{children || 'button'}
		// </ShillyButton>
    <React.Fragment>
      { variant=='primary' && <PrimaryButton>{children}</PrimaryButton> }
      { variant=='secondary' && <SecondaryButton>{children}</SecondaryButton> }



    </React.Fragment>
	);
};

const ShillyButton = styled.button`
  font-weight: bold;
  transition: 200ms ease-out;
  border-radius: var(--br-lg);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  border: none;
  cursor: pointer;
  padding: 16px 24px;
`;

const PrimaryButton = styled(ShillyButton)`
  color: var(--base-10);
  background-color: var(--accent-pink);

  &:hover {
    background-color: var(--accent-pink-darker);
    box-shadow: var(--shadow);
  }
`;

const SecondaryButton = styled(ShillyButton)`
  color: var(--base);
  background-color: var(--base-20);

  &:hover {
    background-color: var(--base-30);
    box-shadow: var(--shadow);
  }
`;

export default Button;
