import background from '../../images/landingPageBg.png';
import axios from 'axios';
import LandingPageFeaturedShillers from './LandingPageFeaturedShillers';
import LandingPageFeaturedTokens from './LandingPageFeaturedTokens';
import ReviewCard from '../../components/ReviewCard';
import Button from '../../components/Button';


function LandingPage() {
    const hero = {
        padding: '60px 0 40px 0',
    }

    const mainTitleHero = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        marginBottom: '100px',
    }

    const heroSubtitle = {
        fontSize: 'var(--fs-b1)',
        color: 'var(--base-60)',
        marginTop: '16px'
    } 

    const header = {
        width: '100%',
        paddingBottom: '20px',
        borderBottom: '1px solid var(--base-30)',
        textAlign: 'left',
        marginBottom: '24px'
    }


    const addToken = (e) => {
        e.preventDefault();
        const testToken = {
            symbol: "ditto",
            name: "Ditto"
        };
        axios.post("http://localhost:5000/token/add", testToken).then(res => console.log('added ' + testToken.name))
    }

    return ( 
        <div style={{ backgroundImage: `url(${background})` }}>
            <div style={{ maxWidth: '1280px', height: '1000px', margin: 'auto', padding: '0 20px' }}>
                <section style={hero}>
                    <div style={mainTitleHero}>
                        <h1 className="fs-display" style={{color: '#490826', maxWidth: '810px'}}>
                            Bring your token <span style={{color: 'var(--accent-pink)'}}>to the moon</span> with the perfect Shiller.
                        </h1>
                        <p style={heroSubtitle}>Shilly is a platform to create, explore and connect within the crypto space.</p>
                    </div>
                </section>

                <Button>Me button</Button>

                <section style={{ display: 'flex', alignItems: 'center', justifyContent: 'left', flexDirection: 'column' }}>
                    <div style={header}>
                        <h2>Featured Shillers</h2>
                    </div>
                    <LandingPageFeaturedShillers />
                </section>


                <section style={{ display: 'flex', alignItems: 'center', justifyContent: 'left', flexDirection: 'column', marginTop: '96px' }}>
                    <div style={header}>
                        <h2>Trending Tokens</h2>
                    </div>
                    <LandingPageFeaturedTokens />

                    <ReviewCard />

                </section>
            </div>
        </div>
    );
}

export default LandingPage;
