import axios from 'axios';
import { Row, Col } from 'react-bootstrap'
import { Component } from 'react';
import Card from '../../components/Card';

export default class LandingPageFeaturedShillers extends Component {
    getShillersLink = "http://localhost:5000/shiller/";

    constructor(props) {
        super(props);
        this.state = { shillers: [] };
    }

    componentDidMount() {
        // axios
        //     .get(this.getShillersLink)
        //     .then((response) => {
        //         this.setState({ shillers: response.data })
        //     })
        //     .catch(function (error) {
        //         console.log(error);
        //     });

        axios.get(`${process.env.REACT_APP_SERVER_URI}/api/auth/getUsers`).then((res) => 
            this.setState({ shillers: res.data.filter(user => user.role)})
        );

    }

    shillerCards() {
        return this.state.shillers.map((shiller) => {
            return (
                <Col key={shiller._id} sm={6} md={4} lg={3}>
                    <Card 
                        shiller={shiller}
                    />
                </Col>
            );
        });
    }

    render() {
        return (
            <Row style={{width: '100%'}}>
                {this.shillerCards()}
            </Row>
        );
    }
}
