import React, { useState } from "react";
import Axios from "axios";
import styled from "styled-components";

const ImageUpload = () => {
  const [fileData, setFileData] = useState();
  const [images, setFile] = useState("");

  const handleFileChange = ({ target }) => {
    setFileData(target.files[0]);
    console.log(target.files[0])
    setFile(target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formdata = new FormData();

    formdata.append("image", fileData);
    console.log("submitting....", fileData);

    await Axios.post(`${process.env.REACT_APP_SERVER_URI}/api/uploadImage/image`, formdata)
      .then((res) => console.log("res RESRES", res.data))
      .catch((error) => console.error(error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <UploadFileArea htmlFor={'upload-button'}></UploadFileArea>
      <input
        id="upload-button"
        type="file"
        value={images}
        name="file"
        accept="image/*"
        onChange={handleFileChange}
        placeholder="upload image"
        style={{display: 'none'}}
      />

      {/* <button>submit</button> */}

      {/* <div style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          borderRadius: "50%",
          border: '1px dashed var(--base-30)',
          width: '95px',
          height: '95px'
      }}>
          <box-icon name='camera' color='var(--base-30)'></box-icon>
      </div> */}
    </form>
  );
};

export default ImageUpload;

const UploadFileArea = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: var(--br-round);
  border: 2px dashed var(--base-30);
  width: 95px;
  height: 95px;
  cursor: pointer;
  transition: var(--transition);

  &:hover {
    border: 2px dashed var(--base-50);
  }
`;
