import background from '../images/landingPageBg.png';

function LandingPage() {
    const hero = {
        padding: '40px 0',
    }

    const mainTitleHero = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        marginBottom: '100px',
    }

    const heroSubtitle = {
        fontSize: 'var(--fs-b1)',
        color: 'var(--base-60)',
        marginTop: '16px'
    } 

    return ( 
        <div style={{ backgroundImage: `url(${background})`, maxWidth: '1280px', height: '1000px' }}>
            <section style={hero}>
                <div style={mainTitleHero}>
                    <h1 className="fs-display" style={{color: '#490826', maxWidth: '810px', marginBottom: '0'}}>
                        Bring your token <span style={{color: 'var(--accent-pink)'}}>to the moon</span> with the perfect Shiller.
                    </h1>
                    <p style={heroSubtitle}>Shilly is a platform to create, explore and connect within the crypto space.</p>
                </div>
            </section>

            <section style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <h2>Trending Campaigns</h2>
                <br></br>
            </section>
        </div>
    );
}

export default LandingPage;
