import Button from "../../components/Button";
import styled from "styled-components";
import profilepic from '../../images/testProfileImg.jpeg';
import coverpic from '../../images/testCoverImg.jpeg';

const instagramLink = 'https://instagram.com/';
const twitterLink = 'https://twitter.com/';
const tiktokLink = 'https://tiktok.com/@';

const formatLink = (link) => {
    return link.includes('https://') ? link.replace('https://', '') : link;
}

const fakeShiller = {
    name: 'Kanye West',
    username: 'iloveducks',
    averagePrice: '10',
    likes: '23',
    profilepic: profilepic,
    coverpic: coverpic,
    bio: 'KANYE2020 ðŸ‡­ðŸ‡°',
    about: `Dogecoin (DOGE) is based on the popular "doge" Internet meme and features a Shiba Inu on its logo. The open-source digital currency was created by Billy Markus from Portland, Oregon and Jackson Palmer from Sydney, Australia, and was forked from Litecoin in December 2013. Dogecoin's creators envisaged it as a fun, light-hearted cryptocurrency that would have greater appeal beyond the core Bitcoin audience, since it was based on a dog meme. Tesla CEO Elon Musk posted several tweets on social media that Dogecoin is his favorite coin.`,
    location: 'Hong Kong',
    website: 'https://KANYEWEST.COM',
    instagram: 'kanyewest',
    twitter: 'kanyewest',
    tiktok: '',
    youtubeLink: 'https://www.youtube.com/channel/UCs6eXM7s8Vl5WcECcRHc2qQ',
    telegramLink: '',
}

function ProfileBasicInfo() {
    return (
        <div style={{display: 'grid', gap: '32px', textAlign: 'start'}}>
            <img src={fakeShiller.profilepic} alt="" style={{width: '200px', height: '200px', borderRadius: 'var(--br-round'}}/>
            <div>
                <h2>{fakeShiller.name}</h2>
                <h3 style={{color: 'var(--base-50)'}}>@{fakeShiller.username}</h3>
            </div>
            <div style={{display: 'flex', width: '100%', justifyContent: 'space-between'}}>
                <div>
                    <h3>S${fakeShiller.averagePrice}</h3>
                    <SecTitle>Average Price</SecTitle>
                </div>
                <div>
                    <h3>S${fakeShiller.averagePrice}</h3>
                    <SecTitle>Average Rating</SecTitle>
                </div>
                <div>
                    <h3>S${fakeShiller.likes}</h3>
                    <SecTitle>Likes</SecTitle>
                </div>
            </div>

            <div style={{display: 'flex', flexDirection: 'row'}}>
                <Button variant='primary' style={{marginRight: '16px'}}>
                    <box-icon type='solid' name='message-rounded' color="var(--base-10)" style={{marginRight: '10px'}}></box-icon>
                    Chat
                </Button>
                <Button variant='tertiary'>
                    <box-icon name='heart' type='solid' ></box-icon>
                </Button>
            </div>

            <UserInfoSection>
                <h3 className="header">Bio</h3>
                {fakeShiller.bio}
                <div style={{display: 'flex', alignItems: 'center'}}>
                    <box-icon name='map' type='regular' style={{marginRight: '8px'}}></box-icon>
                    <span>{fakeShiller.location}</span>
                </div>
            </UserInfoSection>

            <UserInfoSection>
                <h3 className="header">Links</h3>

                { fakeShiller.website && 
                    <SocialMediaLink>
                        <box-icon name="globe"></box-icon>
                        <h4><a href={fakeShiller.website} target="_blank">{formatLink(fakeShiller.website)}</a></h4>
                    </SocialMediaLink> }

                { fakeShiller.instagram && 
                    <SocialMediaLink>
                        <box-icon name='instagram' type='logo' ></box-icon>
                        <h4><a href={instagramLink + fakeShiller.instagram} target="_blank">{fakeShiller.instagram}</a></h4>
                    </SocialMediaLink> }

                { fakeShiller.twitter && 
                <SocialMediaLink>
                    <box-icon name='twitter' type='logo' ></box-icon>
                    <h4><a href={twitterLink + fakeShiller.twitter} target="_blank">{fakeShiller.twitter}</a></h4>
                </SocialMediaLink> }

                { fakeShiller.tiktok && 
                <SocialMediaLink>
                    <box-icon name='tiktok' type='logo' ></box-icon>
                    <h4><a href={tiktokLink + fakeShiller.tiktok} target="_blank">{fakeShiller.tiktok}</a></h4>
                </SocialMediaLink> }

                { fakeShiller.telegramLink && 
                <SocialMediaLink>
                    <box-icon name='telegram' type='logo' ></box-icon>
                    <h4><a href={fakeShiller.telegramLink} target="_blank">Telegram Community</a></h4>
                </SocialMediaLink> }

                { fakeShiller.youtubeLink && 
                <SocialMediaLink>
                    <box-icon name='youtube' type='logo' ></box-icon>
                    <h4><a href={fakeShiller.youtubeLink} target="_blank">YouTube</a></h4>
                </SocialMediaLink> }
            </UserInfoSection>
        </div>
    );
}

const SecTitle = styled.span`
    font-size: var(--fs-b3);
    font-weight: bold;
    color: var(--base-50);
`;

const UserInfoSection = styled.div`
    display: grid;
    gap: 16px;

    & .header {
        padding-bottom: 16px;
        border-bottom: 1px solid var(--base-20);
    }
`;

const SocialMediaLink = styled.div`
    display: grid;
    grid-auto-flow: column;
    gap: 16px;
    align-items: center;
    justify-content: start;
    text-align: start;
`;

export default ProfileBasicInfo;
