import React, { useState } from "react";
import Axios from "axios";
import styled from "styled-components";

const ImageUpload = () => {
  const [fileData, setFileData] = useState();
  const [images, setFile] = useState("");
  const [tempImgUrl, setTempImgUrl] = useState("");

  const handleFileChange = ({ target }) => {
    setFileData(target.files[0]);
    console.log(target.files[0])
    setTempImgUrl(URL.createObjectURL(target.files[0]));
    setFile(target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formdata = new FormData();

    formdata.append("image", fileData);
    console.log("submitting....", fileData);

    await Axios.post(`${process.env.REACT_APP_SERVER_URI}/api/uploadImage/image`, formdata)
      .then((res) => console.log("res RESRES", res))
      .catch((error) => console.error(error));
  };



  return (
    <form onSubmit={handleSubmit}>
      <UploadFileArea htmlFor={'upload-button'} tempImgUrl={tempImgUrl} style={{background: fileData ? `url(${tempImgUrl})` : 'white' }}>
        {!fileData && <box-icon name='camera' color='var(--base-30)'></box-icon>}
        {/* {fileData && <box-icon name='trash-alt' color='var(--base-30)'></box-icon>} */}
        {/* {fileData && <TrashIcon className='bx bx-trash-alt bx-md'></TrashIcon>} */}
        {fileData && <i className="bx bx-trash-alt bx-md trash"></i>}
      </UploadFileArea>
      <input
        id="upload-button"
        type="file"
        value={images}
        name="file"
        accept="image/*"
        onChange={handleFileChange}
        placeholder="upload image"
        style={{display: 'none'}}
      />

      <button>submit</button>
    </form>
  );
};

export default ImageUpload;

const UploadFileArea = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: var(--br-round);
  border: ${props => props.tempImgUrl !== "" ? 'none' : '2px dashed var(--base-30)'};
  width: 95px;
  height: 95px;
  background-size: cover!important;
  cursor: pointer;
  transition: var(--transition);

  &:hover {
    border: ${props => props.tempImgUrl !== "" ? 'none' : '2px dashed var(--accent-pink)'};
    opacity: ${props => props.tempImgUrl !== "" ? '0.7': '1' };
  }

  & .trash {
    color: red;
  }

  &:hover .trash {
    color: green;
  }
`;

const TrashIcon = styled.i`
  color:red;
`;
