import { Row, Col } from "react-bootstrap";
import styled from "styled-components";
import Button from "../../components/Button";
import ReviewCard from "../../components/ReviewCard";
import { useState, useEffect, componentDidMount } from "react";
import axios from "axios";
import placeholderDp from '../../images/placeholderDp.png';

const instagramLink = 'https://instagram.com/';
const twitterLink = 'https://twitter.com/';
const tiktokLink = 'https://tiktok.com/@';

const formatLink = (link) => {
    return link.includes('https://') ? link.replace('https://', '') : link;
}

const fakeShiller = {
    averagePrice: '10',
    likes: '23',
    averageRating: '4.2',
    bio: 'Blockchain Educator | University Instructor| Co-Founder at Blocknia | Content Creator',
    about: `Currently I have the following 3 main focuses:

    - I educate people on Social Media about Blockchain Technology & Cryptocurrencies in both English and Spanish.
    - I teach at the University of Nicosia, providing the first Spanish Online Course about Bitcoin, Blockchain and Cryptocurrencies.
    - I create Educational Content for Crypto companies in two forms: 1) through collaborations with my youtube channels, and 2) for their own Social Media as well.

    Specialties: Cryptocurrencies, Bitcoin, Open Blockchains, Social Media, Communication & Education.`,
    website: 'https://KANYEWEST.COM',
    instagram: 'kanyewest',
    twitter: 'kanyewest',
    tiktok: '',
    youtubeLink: 'https://www.youtube.com/channel/UCs6eXM7s8Vl5WcECcRHc2qQ',
    telegramLink: '',
}


function ProfilePage(props) {
    const [user, setUser] = useState({user: null});
    const [liked, setLiked] = useState(false);

    const { match } = props;
    const currentUser = JSON.parse(localStorage.getItem('currentUser'));
    var viewingOwnProfile = currentUser && (match.params.username === currentUser.username);
    var viewingOtherProfile = match.params.username !== undefined;

    componentDidMount(() => {
        async function f() {
            try {
                if (viewingOtherProfile) {
                    const data = await axios.get(
                        `${process.env.REACT_APP_SERVER_URI}/api/user/getUser`, 
                        { params: {username: match.params.username } });
                    if (data.data.success) {
                        setUser({user: {
                            ...data.data.user
                        }});
                    }
                } else if (viewingOwnProfile) {
                    setUser({user: {
                        ...currentUser
                    }});
                }
            } catch (error) {
                console.log(error)
            }
        }
        f();
        console.log("user", user)
    },  [viewingOtherProfile, viewingOwnProfile, match.params.username, currentUser]);

    const toggleLikeUser = async () => {
        try {
            if (viewingOtherProfile) {
                const config = {
                    header: { "Content-Type": "application/json" },
                };

                var likeeId = user._id;
                var likerId = currentUser._id;

                const { data } = await axios.post(
                    `${process.env.REACT_APP_SERVER_URI}/api/user/like`,
                    { likeeId, likerId },
                    config
                );
                if (data.data.success) {
                    setLiked(true);
                }
            }
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <PageWrap>
            <Row>
                <Col md={4}>                    
                    {/* {ProfileBasicInfo(user.user)} */}
                    <div style={{display: 'grid', gap: '32px', textAlign: 'start'}}>
                        { user && <>
                            <img src={user.user.profileImage ? user.user.profileImage : placeholderDp} alt="" style={{width: '200px', height: '200px', borderRadius: 'var(--br-round', objectFit: 'cover'}}/>
                            <div>
                                <h2>{user.user.name}</h2>
                                <h3 style={{color: 'var(--base-50)'}}>@{user.user.username}</h3>
                            </div>
                            <div style={{display: 'flex', width: '80%', justifyContent: 'space-between'}}>
                                <div>
                                    <h3>S${fakeShiller.averagePrice}</h3>
                                    <SecTitle>Average Price</SecTitle>
                                </div>
                                <div>
                                    <div style={{display: 'flex'}}>
                                        <box-icon name='star' type='solid' color="var(--accent-warning)" style={{marginRight: '8px'}}></box-icon>
                                        <h3>{fakeShiller.averageRating}</h3>
                                    </div>
                                    <SecTitle>Average Rating</SecTitle>
                                </div>
                                <div>
                                    <h3>{fakeShiller.likes}</h3>
                                    <SecTitle>Likes</SecTitle>
                                </div>
                            </div>
                            <div style={{display: 'flex', flexDirection: 'row'}}>
                                <Button variant='primary' style={{marginRight: '16px'}}>
                                    <box-icon
                                        type='solid'
                                        name='message-rounded'
                                        color="var(--base-10)"
                                        style={{marginRight: '10px'}}
                                    ></box-icon>
                                    Chat
                                </Button>
                                <Button variant='secondary' onClick={toggleLikeUser} style={{backgroundColor: liked ? 'var(--accent-pink-a10)' : 'var(--base-20)' }}>
                                    <box-icon name='heart' type='solid' ></box-icon>
                                </Button>
                            </div>
                            <UserInfoSection>
                                <h3 className="header">Bio</h3>
                                {fakeShiller.bio}
                                {user.user.location && <div style={{display: 'flex', alignItems: 'center'}}>
                                    <box-icon name='map' type='regular' style={{marginRight: '8px'}}></box-icon>
                                    <span>{user.user.location}</span>
                                </div>}
                            </UserInfoSection>
                            <UserInfoSection>
                                <h3 className="header">Links</h3>

                                { fakeShiller.website &&
                                    <SocialMediaLink>
                                        <box-icon name="globe"></box-icon>
                                        <h4><a href={fakeShiller.website} target="_blank" rel="noopener noreferrer">{formatLink(fakeShiller.website)}</a></h4>
                                    </SocialMediaLink> }
                                { fakeShiller.instagram &&
                                    <SocialMediaLink>
                                        <box-icon type='logo' name='instagram'></box-icon>
                                        <h4><a href={instagramLink + fakeShiller.instagram} target="_blank" rel="noopener noreferrer">{fakeShiller.instagram}</a></h4>
                                    </SocialMediaLink> }

                                { fakeShiller.twitter &&
                                <SocialMediaLink>
                                    <box-icon type='logo' name='twitter'></box-icon>
                                    <h4><a href={twitterLink + fakeShiller.twitter} target="_blank" rel="noopener noreferrer">{fakeShiller.twitter}</a></h4>
                                </SocialMediaLink> }
                                { fakeShiller.tiktok &&
                                <SocialMediaLink>
                                    <box-icon name='tiktok' type='logo' ></box-icon>
                                    <h4><a href={tiktokLink + fakeShiller.tiktok} target="_blank" rel="noopener noreferrer">{fakeShiller.tiktok}</a></h4>
                                </SocialMediaLink> }
                                { fakeShiller.telegramLink &&
                                <SocialMediaLink>
                                    <box-icon name='telegram' type='logo' ></box-icon>
                                    <h4><a href={fakeShiller.telegramLink} target="_blank" rel="noopener noreferrer">Telegram Community</a></h4>
                                </SocialMediaLink> }
                                { fakeShiller.youtubeLink &&
                                <SocialMediaLink>
                                    <box-icon type="logo" name='youtube'></box-icon>
                                    <h4><a href={fakeShiller.youtubeLink} target="_blank" rel="noopener noreferrer">YouTube</a></h4>
                                </SocialMediaLink> }
                            </UserInfoSection>
                        </> }
                    </div>
                </Col>
                <Col md={8}>
                    <div style={{display: "grid", gap: '64px'}}>
                        <div style={{whiteSpace: 'pre-wrap'}}>
                            <SectionHeader>About</SectionHeader>
                            {fakeShiller.about}
                        </div>

                        <SectionHeader>Completed Collabs</SectionHeader>

                        <SectionHeader>Ongoing Collabs</SectionHeader>

                        <div>
                            <SectionHeader>Reviews as Shiller</SectionHeader>
                            <div style={{display: 'grid', gap: '8px'}}>
                                <ReviewCard />
                                <ReviewCard />
                                <ReviewCard />
                            </div>

                        </div>
                    </div>
                </Col>
            </Row>
        </PageWrap>
    );
}

